import type {ErrorObject, Vocabulary} from "ajv/lib/types"
import limitNumber, {LimitNumberError} from "ajv/lib/vocabularies/validation/limitNumber"
import multipleOf, {MultipleOfError} from "ajv/lib/vocabularies/validation/multipleOf"
import limitLength from "ajv/lib/vocabularies/validation/limitLength"
import pattern, {PatternError} from "ajv/lib/vocabularies/validation/pattern"
import limitProperties from "ajv/lib/vocabularies/validation/limitProperties"
import required, {RequiredError} from "ajv/lib/vocabularies/validation/required"
import limitItems from "ajv/lib/vocabularies/validation/limitItems"
import uniqueItems, {UniqueItemsError} from "ajv/lib/vocabularies/validation/uniqueItems"
import constKeyword, {ConstError} from "ajv/lib/vocabularies/validation/const"
import enumKeyword, {EnumError} from "ajv/lib/vocabularies/validation/enum"

const validation: Vocabulary = [
  // number
  limitNumber,
  multipleOf,
  // string
  limitLength,
  pattern,
  // object
  limitProperties,
  required,
  // array
  limitItems,
  uniqueItems,
  // any
  {keyword: "type", schemaType: ["string", "array"]},
  {keyword: "nullable", schemaType: "boolean"},
  constKeyword,
  enumKeyword,
]

export default validation

type LimitError = ErrorObject<
  "maxItems" | "minItems" | "minProperties" | "maxProperties" | "minLength" | "maxLength",
  {limit: number},
  number | {$data: string}
>

export type ValidationKeywordError =
  | LimitError
  | LimitNumberError
  | MultipleOfError
  | PatternError
  | RequiredError
  | UniqueItemsError
  | ConstError
  | EnumError
